          commit,
          url: `${BASE_POST_URL}/${payload.id}`,
          doHideAlert: true,
          success(result) {
            // 保存文章
            commit(LOAD_POST, result.doc)
            // 向前端通知操作成功
            resolve(result.doc)
          },
          fail(err) {
            // 向前端通知操作失败
            reject(err)
          }
        })
      })
    },
    /* 获取所有文章 */
    [LOAD_POSTS_ASYNC]({ commit }) {
      return new Promise((resolve, reject) => {
        this._vm.$axios({
          commit,
          url: `${BASE_POST_URL}`,
          doHideAlert: true,
          success(result) {
            // 保存文章
            commit(LOAD_POSTS, result.docs)
            // 向前端通知操作成功
            resolve(result.docs)
          },
          fail(err) {
            // 向前端通知操作失败
            reject(err)
          }
        })
      })
    }
  },
  mutations: {
    /* 保存所有文章信息 */
    [LOAD_POSTS](state, payload) {
      state.docs = payload
    },
    /* 保存当前文章信息 */
    [LOAD_POST](state, payload) {
      state.doc = payload
    },
    /* 清空当前文章信息 */
    [CLEAR_POST](state) {
      state.doc = null
    }
  }
}
